import React, { Component } from 'react';
import './../CSS/Timeline.css'
import NewsFeed from './NewsFeed';

import './../../node_modules/bootstrap/dist/css/bootstrap.min.css'


class Timeline extends Component {

    constructor(props) {
        super(props);
        this.state = {
            posts: [],

        };

    }

    componentDidMount() {

        fetch("https://newsapi.org/v2/top-headlines?country=in&apiKey=20154dc2da4d4cabbd1bcda1bc8bc69b")
            .then(response => {
                return response.json();
            }).then(result => {
                //console.log(result.articles);
                this.setState({
                    posts: result.articles
                })
            })
            .catch((error) => {
                console.log(error);
            });

    }

    renderPost = () => {
        const postElement = this.state.posts.map((post, index) =>
            <NewsFeed key={index} value={post} />
        );
        return postElement;
    }


    render() {
        console.log(this.state.posts.length > 0 ? this.state.posts[0].title : '')

        return (
            <>
            <nav class="navbar fixed-top navbar-dark bg-dark justify-content-between">
                <a class="navbar-brand">Navbar</a>
                <form class="form-inline">
                    <input class="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" />
                    <button class="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
                </form>
                </nav>
            <div>
                {this.renderPost()}
            </div>
            </>
        )
    }

}

export default Timeline;
