import React, { Component } from 'react';
import './../CSS/Timeline.css'
import NewsFeed from './NewsFeed';
import NavBar from './NavBar';
import Loading from './Loading';

import './../../node_modules/bootstrap/dist/js/bootstrap.min.js'




class Timeline extends Component {

    constructor(props) {
        super(props);
        this.state = {
            posts: [],
            url: "https://newsapi.org/v2/top-headlines?country=in&apiKey=20154dc2da4d4cabbd1bcda1bc8bc69b",
            loaded: 0
        };

    }


    apiRef = (urlRef) => {
        this.setState({
            url: urlRef
        })
    }

    fetchHandler = () => {
        fetch(this.state.url)
            .then(response => {
                return response.json();
            }).then(result => {
                this.setState({
                    posts: result.articles,
                    loaded: 1
                })
            })
            .catch((error) => {
                console.log(error);
                this.setState({
                    loaded: 2
                })
            });

    }

    componentDidMount() {

        this.fetchHandler();

    }


    componentDidUpdate(prevProps, prevState){
        //console.log(prevState.url)
        // if(prevState.url !== this.state.url || this.state.posts === null){
        //     console.log(this.state.url);
        //     this.fetchHandler();
        // }
        this.fetchHandler();
        console.log(this.state.posts);
    }

    searchQuery = (query, type) => {
        if(type === "search"){
            this.apiRef("https://newsapi.org/v2/everything?q=" + query + "&apiKey=20154dc2da4d4cabbd1bcda1bc8bc69b");
        }
        else{
            this.apiRef("https://newsapi.org/v2/sources?category=business&apiKey=20154dc2da4d4cabbd1bcda1bc8bc69b");
        }
    }

    categoryQuery = (category) => {
        this.setState({
            url: "https://newsapi.org/v2/sources?&apiKey=20154dc2da4d4cabbd1bcda1bc8bc69b"
        });   
    }

    renderPost = () => {

        if(this.state.posts === undefined){
            return (<h1 class="NotLoaded">Sorry Could not Load Anything</h1>);
        }
        else if(this.state.loaded === 2){
            return (<h1 class="NotLoaded">Internet Not Working</h1>)
        }
        else{
            const postElement = this.state.posts.map((post, index) =>
            <NewsFeed key={index} value={post}></NewsFeed>
        );
        return postElement;
        }
    }


    render() {
        console.log(this.state.posts)
        return (
            <>
            <NavBar queryHandler = {this.searchQuery} categoryHandler = {this.categoryQuery}/>
            <div class="container">
                {this.state.loaded === 0 ? <Loading /> : ""}
            </div>
            <div class="container newsFeeds">
                {this.renderPost()}
            </div>
            </>
        )
    }

}

export default Timeline;
