import React, { Component } from 'react';
import './../CSS/Timeline.css'
import NewsFeed from './NewsFeed';

import './../../node_modules/bootstrap/dist/css/bootstrap.min.css'
import './../../node_modules/popper.js'

import './../../node_modules/bootstrap/dist/js/bootstrap.min.js'
import './../../node_modules/jquery'


class Timeline extends Component {

    constructor(props) {
        super(props);
        this.state = {
            posts: [],

        };

    }

    componentDidMount() {

        fetch("https://newsapi.org/v2/top-headlines?country=in&apiKey=20154dc2da4d4cabbd1bcda1bc8bc69b")
            .then(response => {
                return response.json();
            }).then(result => {
                //console.log(result.articles);
                this.setState({
                    posts: result.articles
                })
            })
            .catch((error) => {
                console.log(error);
            });

    }

    renderPost = () => {
        const postElement = this.state.posts.map((post, index) =>
            <NewsFeed key={index} value={post} />
        );
        return postElement;
    }


    render() {
        console.log(this.state.posts.length > 0 ? this.state.posts[0].title : '')

        return (
            <>
            <nav class="navColor navbar fixed-top bg-light">

                <a class="navbar-brand">Navbar</a>
                <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                    <span class="navbar-toggler-icon"></span>
                </button>
                <div class="collapse navbar-collapse" id="navbarSupportedContent">
                    <form class="form-inline">
                        <input class="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" />
                        <button class="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
                    </form>
                </div>

            </nav>
            <div>
                {this.renderPost()}
            </div>
            </>
        )
    }

}

export default Timeline;
