import React, { Component } from 'react';
import './../CSS/Timeline.css'
import NewsFeed from './NewsFeed';
import NavBar from './NavBar';


import './../../node_modules/bootstrap/dist/js/bootstrap.min.js'



class Timeline extends Component {

    constructor(props) {
        super(props);
        this.state = {
            posts: [],

        };

    }

    componentDidMount() {

        fetch("https://newsapi.org/v2/top-headlines?country=in&apiKey=20154dc2da4d4cabbd1bcda1bc8bc69b")
            .then(response => {
                return response.json();
            }).then(result => {
                //console.log(result.articles);
                this.setState({
                    posts: result.articles
                })
            })
            .catch((error) => {
                console.log(error);
            });

    }

    searchQuery = (query) => {
        fetch("https://newsapi.org/v2/everything?q=" + query + "&apiKey=20154dc2da4d4cabbd1bcda1bc8bc69b")
            .then(response => {
                return response.json();
            }).then(result => {
                //console.log(result.articles);
                this.setState({
                    posts: result.articles
                })
            })
            .catch((error) => {
                console.log(error);
            });
    }

    renderPost = () => {
        const postElement = this.state.posts.map((post, index) =>
            <NewsFeed key={index} value={post} clickHandler = {this.searchQuery}/>
        );
        return postElement;
    }


    render() {
        console.log(this.state.posts.length > 0 ? this.state.posts[0].title : '')

        return (
            <>
            <NavBar />
            <div class="container newsFeeds">
                {this.renderPost()}
            </div>
            </>
        )
    }

}

export default Timeline;
