import React, { Component } from 'react';
import './../CSS/NavBar.css'

import './../../node_modules/bootstrap/dist/css/bootstrap.min.css'
import './../../node_modules/jquery'
import './../../node_modules/popper.js'


class NavBar extends Component {

    searchHandler = () => {
        console.log('working in navbar' + document.getElementById('query').value);
        this.props.clickHandler(document.getElementById('query').value);
    }

    render() {
        return (
            <nav class="navColor navbar navbar-expand-lg navbar-dark bg-dark fixed-top">

                <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">

                    <span class="navbar-toggler-icon"></span>
                </button>

                <div class="collapse navbar-collapse" id="navbarSupportedContent">
                <a class="navbar-brand">TopNews</a>
                <li class="nav-item dropdown">
                    <a class="nav-link dropdown-toggle" href="#" id="navbarDropdown" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                    Dropdown
                    </a>
                    <div class="dropdown-menu" aria-labelledby="navbarDropdown">
                        <a class="dropdown-item" href="#">Action</a>
                        <a class="dropdown-item" href="#">Another action</a>
                    </div>
                </li>
                <div class="right">
                        <input class="searchBar" id ="query" type="search" placeholder="Search" aria-label="Search" />
                        <button class="btn btn-outline-primary" type="button" 
                        onClick = {() => {
                            this.props.clickHandler(document.getElementById('query').value);
                        }}>Search</button>
                        </div>
                </div>

            </nav>
        )
    }
}

export default NavBar;
